import { StyleSheet } from 'react-native';

const styles = StyleSheet.create({
    container: {
        backgroundColor: '#3671EE',
        padding: 10,
        borderTopLeftRadius: 10,
        borderTopRightRadius: 10,
    },
    title: {
        color: '#fff',
        fontSize: 20,
        fontWeight: 'bold',
        marginBottom: 10
    },
    text: {
        color: '#bed9ff',
        fontSize: 15,
        marginBottom: 10,
    },
    learnMore: {
        color: '#fff',
        fontSize: 15,
        fontWeight: 'bold',
    }
})

export default styles;
